import { WebSocketService } from '../web-socket.service';
import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { fromEvent, merge } from 'rxjs';


@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css'],
})
export class ChatComponent implements OnInit, OnDestroy {
  constructor(private webSocketService: WebSocketService) {}

  messages: any[] = [];
  messageText: string = '';
  privateMessageModalVisible: boolean = false;
  privateMessages: any[] = [];


  sendMessage() {
    console.log('sendMessage called');
    const username = localStorage.getItem('username');
    if (this.messageText.trim() !== '') {
      console.log('Sending message:', { text: this.messageText, username });
      this.webSocketService.emit('message', { text: this.messageText, username });
      this.messageText = '';
    }
  }

  sendPrivateMessage(recipient: string, text: string) {
    this.webSocketService.emit("privateMessage", { recipient, text });
  }

  ngOnInit(): void {
    // Registra o nome de usuário no servidor
    const username = localStorage.getItem('username');
    this.webSocketService.emit('register', username);

    // Combina os observáveis dos eventos 'messageHistory', 'message' e 'privateMessage'
    const messageHistory$ = this.webSocketService.fromEvent<any[]>('messageHistory');
    const message$ = this.webSocketService.fromEvent<any>('message');
    const privateMessage$ = this.webSocketService.fromEvent<any>('privateMessage');

    merge(messageHistory$, message$, privateMessage$).subscribe((data) => {
      if (Array.isArray(data)) {
        console.log(1);
        this.messages = data;
      } else {
        if (data.sender) {
          console.log(3);
          this.privateMessages.push(data);
        } else {
          console.log(2);
          this.messages.push(data);
        }
      }
    });
  }



  ngOnDestroy(): void {
    this.webSocketService.disconnect();
  }
}
<div class="chat-container">
  <button class="private-message-btn" (click)="privateMessageModalVisible = true">Enviar mensagem privada</button>
  <app-private-message-modal *ngIf="privateMessageModalVisible" #privateMessageModal (privateMessageSent)="sendPrivateMessage($event.recipient, $event.text)" (modalClosed)="privateMessageModalVisible = false"></app-private-message-modal>

  <div class="message-list">
    <div *ngFor="let message of messages" class="message">
      <strong>{{ message.username }}:</strong> {{ message.text }}
    </div>

    <div *ngFor="let privateMessage of privateMessages" class="private-message">
      <strong>(Privado) {{ privateMessage.sender }} -> {{ privateMessage.recipient }}:</strong> {{ privateMessage.text }}
    </div>
  </div>

  <form class="send-message-form" (submit)="sendMessage()">
    <input type="text" [(ngModel)]="messageText" placeholder="Type your message" name="chat"/>
    <button type="submit">Send</button>
  </form>
</div>
