import { Component } from '@angular/core';
import { WebSocketService } from '../web-socket.service';


@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent {
  constructor(private webSocketService: WebSocketService) {}

  messages: string[] = [];
messageText = '';

sendMessage() {
  const username = localStorage.getItem('username'); // Adicione esta linha
  if (this.message.trim() !== '') {
    this.socket.emit('message', { text: this.message, username }); // Adicione o nome de usuário ao objeto da mensagem
    this.message = '';
  }
}

ngOnInit(): void {
  this.webSocketService.on('message', (message: string) => {
    this.messages.push(message);
  });
}

ngOnDestroy(): void {
  this.webSocketService.disconnect();
}

}
