import { Component } from '@angular/core';
import { AuthService } from '../auth.service';
import { Router } from '@angular/router'; // Importe o serviço Router

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  username: string = '';
  password: string = '';

  constructor(
    private authService: AuthService,
    private router: Router // Injete o serviço Router
  ) {}

  login() {
    this.authService.login(this.username, this.password).subscribe(
      (response: any) => {
        console.log('Login bem-sucedido', response);
        // Armazenar o token JWT no localStorage
        localStorage.setItem('authToken', response.token);
        // Redirecionar para a página de chat ou outra rota protegida
        this.router.navigate(['/chat']);
      },
      (error) => {
        console.error('Erro no login', error);
        // Mostrar mensagem de erro na interface do usuário
      }
    );
  }
}
<form (submit)="login()" class="login-form">
  <div class="form-group">
    <label for="username" class="form-label">Usuário</label>
    <input type="text" id="username" [(ngModel)]="username" name="username" required class="form-input" />
  </div>
  <div class="form-group">
    <label for="password" class="form-label">Senha</label>
    <input type="password" id="password" [(ngModel)]="password" name="password" required class="form-input" />
  </div>
  <div>
    <button type="submit" class="submit-btn">Entrar</button>

  </div>
</form>
